var Lava = require('./lib/class_manager.js');

Lava.ClassManager.registerRootNamespace('global', global);
Lava.ClassManager.define(
'global.LavaA',
{
	val: null,
	x: null,
	y: null,
	z: null,
	ggg: [function() {}],
	init: function(val) {
		this.val = val;
	},
	method1: function(x, y, z) {
		this.x = x;
		this.y = y;
		this.z = z;
	}
});

new LavaA();

console.log(LavaA + '');